from Bio.Seq import Seq

def base_counter(dna):
    A = 0
    T = 0
    C = 0
    G = 0
    for char in dna:
        if char == 'A':
            A = A + 1
        elif char == 'T':
            T = T + 1
        elif char == 'C':
            C = C + 1
        elif char == 'G':
            G = G + 1
    return f"{A} {C} {G} {T}"
    
